          "Profil_": "P1",
          "Position_": "B",
          "Datum_": "10_04_19",
          "Phase_": "Phase 04",
          "Uhrzeit_": "14:47",
          "Thema_": "Gold Forum\nZürich\nSchweiz\nAktivismus",
          "GesinnungX_": "Links",
          "GesinnungY_": "Mitte",
          "Verlässlichkeit_": "Reisserisch",
          "Art_": "Zeitung",
