          "Profil_": "P1",
          "Position_": "C",
          "Datum_": "11_04_19",
          "Phase_": "Phase 04",
          "Uhrzeit_": "11:40",
          "Thema_": "GPS\nGLP\nRegierungsrat\nMartin Neukom\nSchweiz",
