          "Profil_": "P1",
          "Position_": "F",
          "Datum_": "24_04_19",
          "Phase_": "Phase 06",
          "Uhrzeit_": "15:34",
          "Thema_": "Bildung\nDeutschland\nPolitik\n\n",
          "GesinnungX_": "Links",
          "GesinnungY_": "Liberal",
          "Verlässlichkeit_": "Reisserisch",
          "Art_": "Zeitung",
