          "Profil_": "P2",
          "Position_": "E",
          "Datum_": "01_04_19",
          "Phase_": "Phase 02",
          "Uhrzeit_": "14:41",
          "Thema_": "Turkei\nErdogan\nAKP\n",
          "GesinnungX_": "Mitte",
