          "Position_": "E",
          "Datum_": "10_04_19",
          "Phase_": "Phase 04",
          "Uhrzeit_": "11:32",
          "Thema_": "Deutschland\nAngela Merkel\nJohn Mickethwait\nEuropa System",
          "GesinnungX_": "Mitte",
          "GesinnungY_": "Konservativ",
          "Verlässlichkeit_": "Analytisch",
