          "Profil_": "P2",
          "Position_": "F",
          "Datum_": "19_04_19",
          "Phase_": "Phase 05",
          "Uhrzeit_": "13:47",
          "Thema_": "Schweiz\nSVP\nSieg\nWerner Salzmann\n",
          "GesinnungX_": "Rechts",
          "GesinnungY_": "Konservativ",
          "Verlässlichkeit_": "Reisserisch",
