function createTextbox(element, id, content, array, fontSize) {
    var item = doc.pages.item(currentPage).textFrames.add();
    item.contents = content || ' ';
    item.recompose();
    item.fit(FitOptions.frameToContent);
    item.name = 'element_' + id + '_textbox_' * element;
    item.paragraphs[0].pointSize = fontSize;
    array.push(item);

    var text = item.paragraphs[0];
    text.appliedFont = app.fonts.item("Nimbus Roman Mono M");
    text.fontStyle = "Regular";

    return item;
}


function positionElement( left, element, x, y) {
    // element.recompose();
    element.fit(FitOptions.frameToContent);

    x = left ? x + 8 : x + page.bounds[3] -  page.bounds[1] + 12;
    
    var elementWidth = element.geometricBounds[3] -  element.geometricBounds[1];
    var elementHeight = element.geometricBounds[2] -  element.geometricBounds[0];

    element.geometricBounds = [y, x, y + elementHeight, x + elementWidth];

    if(left) {
        lowestLeft = getElement(element).y2;
    }
    else {
        lowestRight = getElement(element).y2;
    }

    phaseHeight = lowestLeft > lowestRight ? lowestLeft : lowestRight;
}

function getElement(element) {

    var item = {
        x: element.geometricBounds[1],
        y: element.geometricBounds[0],
        x2: element.geometricBounds[3],
        y2: element.geometricBounds[2],
        width: element.geometricBounds[3] -  element.geometricBounds[1],
        height: nameHeight = element.geometricBounds[2] -  element.geometricBounds[0],
    }

    return item;
}

function setDimensions(left, element, width, height, spacingRight, spacingBottom) {

    var dimensions = element.geometricBounds;

    if (width) {
        element.geometricBounds = [dimensions[0], dimensions[1], dimensions[2], dimensions[1] + width];
    }

    dimensions = element.geometricBounds;

    if (spacingRight) {
        element.geometricBounds = [dimensions[0], dimensions[1], dimensions[2], dimensions[1] + spacingRight];
    }

    element.fit(FitOptions.frameToContent);
    dimensions = element.geometricBounds;

    if (height) {
        element.geometricBounds = [dimensions[0], dimensions[1], dimensions[2] + height, dimensions[3] ];
    }

    dimensions = element.geometricBounds;


    if (spacingBottom) {
        element.geometricBounds = [dimensions[0], dimensions[1], dimensions[2] + spacingBottom, dimensions[3] ];
    }

    if(left) {
        lowestLeft = getElement(element).y2;
    }
    else {
        lowestRight = getElement(element).y2;
    }
    
    phaseHeight = lowestLeft > lowestRight ? lowestLeft : lowestRight;

}

function getText(fullText, type) {

    // var partial = fullText.split('*' + type + '*').pop().split("\n")[0] || 'unknown';
    var partial = fullText.split('*' + type + '*').pop().split('*/' + type + '*')[0] || 'unknown';
    return partial;
}

function getMultipleTexts(fullText, type) {

    // var partials = fullText.split('*' + type + '*')[2] || 'unknown';
    // var partials = fullText.split('*' + type + '*').pop().split('*/' + type + '*')[0];

    var partial = fullText.split('*' + type + '*');
    var result = [];
    for (var i = 0; i < partial.length; i++) {

        var split = partial[i].split('*/' + type + '*');
        if (split.length > 1) {
            result.push(split[0]);
        }
    }

    // alert(result);

    return result;

    // return partials;
}

function getArticleBodyText(fullText) {

  var articleBodyText = [];
  var partial = fullText.split('*al*');

  for (var i = 0; i < partial.length; i++) {
      var split = partial[i].split('*/al*');
      if (split.length > 1) {
        articleBodyText.push({
          type: 'lead',
          content: split[0]
        });
      }

    var miniPartial = partial[i].split('*ab*');

    for (var j = 0; j < miniPartial.length; j++) {

      var miniSplit = miniPartial[j].split('*/ab*');
      if (miniSplit.length > 1) {
        articleBodyText.push({
          type: 'text',
          content: miniSplit[0]
        });

      } 
    }
  }



  return articleBodyText;

}


function addLine(left, x, y, length, array) {

    x = left ? x + 8 : x + page.bounds[3] -  page.bounds[1] + 12;    
       
    var myLine = doc.pages.item(currentPage).graphicLines.add();  
    
    myLine.strokeColor = app.activeDocument.colors.item("Black");  
    myLine.strokeWeight = 0.75; 
    
  
    plotStraightLine(myLine, x, y, x + length, y); 

    array.push(myLine);

}

  
  
function plotStraightLine(obj, x1, y1, x2, y2) {  
    obj.paths.item(0).pathPoints.item(0).anchor = [x1, y1];  
    obj.paths.item(0).pathPoints.item(1).anchor = [x2, y2];  
}  

function checkHeight(left, item) {

    var itemHeight = getElement(item).y2 - borderTopBottom;
    var itemData = getElement(item);

    if(itemHeight > pageHeight) {
        if(left) {
            if (currentLeftPage >= lastPage - 1) {
                doc.pages.add();
                doc.pages.add();
                lastPage += 2;
            }

            currentLeftPage += 2;
            currentPage = currentLeftPage;
            lowestLeft = borderTopBottom;
            newPageLeft = true;
            item.move(doc.pages[currentLeftPage])
            positionElement(true, item, itemData.x - 8, borderTopBottom, 0, 0)

        } else if(!left) {
            if (currentRightPage >= lastPage) {
                doc.pages.add();
                doc.pages.add();
                lastPage += 2;
            }

            currentRightPage += 2;
            currentPage = currentRightPage;
            lowestRight = borderTopBottom;
            newPageRight = true;
            item.move(doc.pages[currentRightPage])
            positionElement(false, item, itemData.x - (page.bounds[3] - page.bounds[1] + 12), borderTopBottom, 0, 0)
        }

    }
}

function createHeader(positioningElement, persona, date, groupArray, left) {

    var header_phase = createTextbox('header_phase', 'header', 'Phase ' + phase, groupArray, 8);
    header_phase.paragraphs[0].appliedParagraphStyle = styleKopfzeileLinks;
    positionElement( left, header_phase, 0, getElement(positioningElement).y2 + 10, 0, 0);
    setDimensions(left, header_phase, pageWidth/3, getElement(header_phase).height + 8.5, 0, 0);

    var header_persona = createTextbox('header_persona', 'header', 'PERSONA ' + persona, groupArray, 8);
    header_persona.paragraphs[0].appliedParagraphStyle = styleKopfzeileZentriert;
    positionElement( left, header_persona, pageWidth / 3, getElement(positioningElement).y2 + 10, 0, 0);
    setDimensions(left, header_persona, pageWidth/3, getElement(header_persona).height + 8.5, 0, 0);

    var header_datum = createTextbox('header_datum', 'header', date, groupArray, 8);
    header_datum.paragraphs[0].appliedParagraphStyle = styleKopfzeileLinks;
    positionElement( left, header_datum, pageWidth/3 * 2, getElement(positioningElement).y2 + 10, 0, 0);
    setDimensions(left, header_datum, pageWidth/3, getElement(header_datum).height + 8.5, 0, 0);

    checkHeight(left, header_phase);
}

function createImageGrid(left) {
    
    var distance = 30;
    var array = left ? imagesRH : imagesDD;
    var positionY = left ? lowestLeft : lowestRight;
    var emptyArray = [];
    var currentGridPage = left ? lastPage - 1 : lastPage;
    var border = left ? 8 : page.bounds[3] - page.bounds[1] + 12;

    for (var y = 0; y < array.length; y ++) {

        for (var x = 0; x < array[y].length; x ++) {
            if (array[y] && array[x][y]) {
                if (array[x][y].link) {
                    var image = addImage(array[x][y].link, border + pageWidth/5 * y, positionY + 30 * x, pageWidth/7, currentGridPage);
                }
                var name = createTextbox(name, x, array[x][y].name, emptyArray, 8 );
                name.move(doc.pages[currentGridPage])
                positionElement( left, name, pageWidth/7 * y, positionY + 30 * x + 20, 0, 0);
                setDimensions(left, name, pageWidth/7, 0, 0, 0);
            }
        }   
    }

}


function createCaptionGrid(left) {
    
    var array = left ? imagesRH : imagesDD;
    var positionY = left ? lowestLeft : lowestRight;
    var emptyArray = [];
    var page = left ? lastPage - 1 : lastPage;

    for (var y = 0; y < array.length; y ++) {
      var rowY = positionY;
      for (var x = 0; x < array[y].length; x ++) {
        if (array[y] && array[x][y]) {
          var name = createTextbox(name, x, array[x][y].name, emptyArray, 8 );
          name.move(doc.pages[page])
          positionElement( left, name, pageWidth/5 * y, rowY, 0, 0);
          setDimensions(left, name, pageWidth/5, null , 0, 0);
          // checkHeight(left, name);
          rowY = getElement(name).y2;

          var text = createTextbox(text, x, array[x][y].text, emptyArray, 8 );
          text.move(doc.pages[page])
          positionElement( left, text, pageWidth/5 * y, rowY + 5, 0, 0);
          setDimensions(left, text, pageWidth/5, null , 0, 0);
          // checkHeight(left, text);
          rowY = getElement(text).y2;

          if(left) {
              lowestLeft = getElement(text).y2 > lowestLeft ? getElement(text).y2 : lowestLeft
          } else {
              lowestRight = getElement(text).y2 > lowestRight ? getElement(text).y2 : lowestRight
          }
      }
      }
    }    

}